mapper.select:
SELECT * FROM board
WHERE bid = #{bid}
AND bno = #{bno}

mapper.update:
UPDATE board SET viewcnt = viewcnt + 1
WHERE bid = #{bid}
AND bno = #{bno}

UPDATE board SET
title = #{title},
content = #{content}
WHERE bid = #{bid}
AND bno = #{bno}
AND password = #{password}

UPDATE board SET
title = #{title},
content = #{content},
file_name = #{file_name},
file_rename = #{file_rename},
file_size = #{file_size}
WHERE bid = #{bid}
AND bno = #{bno}
AND password = #{password}

mapper.delete:
DELETE FROM board
WHERE bid = #{bid}
AND bno = #{bno}
AND password = #{password}
